import React from 'react';
import './App.css';

class Pokemon extends React.Component {
  render() {
    const { name, type, averageWeight, image } = this.props.pokemon;

    return (
      <div>
        <p>
          <strong>{name}</strong>
        </p>
        <p>{type}</p>
        <p>
          {averageWeight.value}
          {averageWeight.measurementUnit}
        </p>

      </div>
    );
  }
}

export default Pokemon;
